
CREATE TABLE IF NOT EXISTS MAPA (
    id INT AUTO_INCREMENT PRIMARY KEY,
    area VARCHAR(100),
    nivel VARCHAR(10),
    enunciado TEXT,
    ejemplo TEXT
);

CREATE TABLE IF NOT EXISTS EVIDENCIAS_DE_APRENDIZAJE (
    id INT AUTO_INCREMENT PRIMARY KEY,
    mapa_id INT,
    evidencia TEXT,
    FOREIGN KEY (mapa_id) REFERENCES MAPA(id)
);

